import { GridDefaultProps } from './props';
Component({
    props: GridDefaultProps,
    methods: {
        onTap: function (e) {
            var item = e.target.dataset.item;
            var onTap = this.props.onTap;
            if (onTap) {
                onTap(item);
            }
        },
        onFirstAppear: function (e) {
            var item = e.target.dataset.item;
            var onFirstAppear = this.props.onFirstAppear;
            if (onFirstAppear) {
                onFirstAppear(item);
            }
        },
    },
});
